mapper.update:
UPDATE jy_base_scan_gun
SET isValid = #{isValid},
	orgId = #{orgId},
	updateTime = #{updateTime},
	description = #{description}
WHERE id = #{gunId}

mapper.select:
       	SELECT COUNT(*)
		FROM jy_base_scan_gun gun
       	WHERE gun.gunNo=#{gunNo}
